from django.db import modelsfrom .base import BaseEntityclass Category(BaseEntity):    name = models.CharField(max_length=70)    is_active = models.BooleanField(default=True)    def __str__(self):        return self.nameclass Article(BaseEntity):    title = models.CharField(max_length=150)    category = models.ForeignKey(Category, on_delete=models.CASCADE, related_name='article_category')    description = models.TextField()    thumbnail = models.ImageField(upload_to='article')    def __str__(self):        return self.title