from django.db import modelsfrom .base import BaseEntityfrom blog.utils import ArticleStatusfrom enumchoicefield import EnumChoiceFieldclass Category(BaseEntity):    name = models.CharField(max_length=70)    is_active = models.BooleanField(default=True)    def __str__(self):        return self.nameclass Article(BaseEntity):    title = models.CharField(max_length=150)    category = models.ForeignKey(Category, on_delete=models.CASCADE, related_name='article_category')    description = models.TextField()    is_active = EnumChoiceField(ArticleStatus, default=ArticleStatus.PENDING)    thumbnail = models.ImageField(upload_to='article', blank=True)    def __str__(self):        return self.title[:15]