from django.shortcuts import render, get_object_or_404from django.views import Viewfrom django.views.generic import CreateView, ListViewfrom blog.models.article import Articlefrom blog.forms.article_form import ArticleFormclass CreateArticleView(CreateView):    template_name = 'article/create_article.html'    model = Article    form_class = ArticleFormclass ArticleListView(ListView):    model = Article    template_name = 'article/article_list.html'    context_object_name = 'order_list'class DetailView(View):    def get(self, request, title):        details = get_object_or_404(Article, title=title)        context = {            'detail': details        }        template_name = 'details.html'        return render(request, template_name, context)